export class SearchHeaderQHtmlBox{userLogin

    constructor(target,position,placeholder,placeholderFocus,userLogin){let name="SearchHeaderQHtmlBox",url="/PageComponents/"+name,urlContent=url+"/content",css=document.createElement("link");css.setAttribute("rel","stylesheet");css.setAttribute("href",url+"/style.min.css");document.head.append(css);this.userLogin=userLogin;let html='        <div id="'+name+'">            <input type="text" placeholder="'+placeholder+'" />            <a>                <img src="'+urlContent+'/search.png" />            <\/a>        <\/div>';target.insertAdjacentHTML(position,html);document.querySelector("#"+name+' input[type="text"]').addEventListener("focus",()=>{document.querySelector("#"+name+' input[type="text"]').setAttribute("placeholder",placeholderFocus)});document.querySelector("#"+name).addEventListener("blur",()=>{document.querySelector("#"+name+' input[type="text"]').setAttribute("placeholder",placeholder)});document.querySelector("#"+name+" > a").addEventListener("click",()=>{let search=document.querySelector("#"+name+" input").value.toLowerCase();this.userLogin.length>0&&(search="-"+this.userLogin+"-"+search);search.length>0&&(window.location.href="/i/"+search)});document.querySelector("#"+name+" input").addEventListener("keypress",async event=>{if(event.key=="Enter"){let search=document.querySelector("#"+name+" input").value.toLowerCase();this.userLogin.length>0&&(search="-"+this.userLogin+"-"+search);search.length>0&&(window.location.href="/i/"+search)}})}}